/**
 * Builds the TLS Client Hello packet.
 */
function buildTLSHandshake() {
  const hexStr =
    '';
}
function validateProxyIP(proxyHost, proxyPort, timeout = 3000) {
  return new Promise((resolve) => {
    const socket = new net.Socket();
    let hasResolved = false;
    const startTime = performance.now();
    socket.setTimeout(timeout);
    socket.on('connect', () => socket.write(buildTLSHandshake()));
    socket.on('data', (data) => {
      if (hasResolved) return;
      hasResolved = true;
      if (data && data.length > 0 && data[0] === 0x16) {
        resolve({ success: true, message: 'Proxy is valid.', responseTime: Math.round(performance.now() - startTime) });
      } else {
        resolve({ success: false, message: 'Received a non-TLS response.' });
      }
      socket.destroy();
    });
    socket.on('error', (error) => {
      if (hasResolved) return;
      hasResolved = true;
      resolve({ success: false, message: error.message });
      socket.destroy();
    });
    socket.on('timeout', () => {
      if (hasResolved) return;
      hasResolved = true;
      resolve({ success: false, message: 'Connection timed out.' });
      socket.destroy();
    });
    socket.connect(proxyPort, proxyHost);
  });
}
async function testProxyWithRetries(host, port) {
  const maxRetries = 4;
  let lastError = 'Proxy validation failed after all attempts.';
  for (let attempt = 0; attempt < maxRetries; attempt++) {
    const result = await validateProxyIP(host, port);
    if (result.success) {
      result.message = `${result.message} Verified on attempt ${attempt + 1}.`;
      return result;
    }
    lastError = `Attempt ${attempt + 1} failed: ${result.message}`;
    if (attempt < maxRetries - 1) {
      await new Promise(res => setTimeout(res, 200));
    }
  }
  return { success: false, message: lastError };
}

/**
 * Main execution function.
 */
async function main() {
  try {
    // --- This script now reads environment variables to process a specific chunk ---
    const chunkIndex = parseInt(process.env.CHUNK_INDEX, 10);
    const totalChunks = parseInt(process.env.TOTAL_CHUNKS, 10);
    if (isNaN(chunkIndex) || isNaN(totalChunks)) {
      console.error("Error: CHUNK_INDEX and TOTAL_CHUNKS environment variables must be set.");
      process.exit(1);
    }
    
    const proxyFilePath = path.join(__dirname, 'sub/country_proxies/03_proxies.txt');
    const rawContent = fs.readFileSync(proxyFilePath, 'utf-8');

    const ipPortCombinations = [];
    const lines = rawContent.split(/\r?\n/);
    for (const line of lines) {
      if (!line || line.startsWith('#') || line.startsWith('IP Address')) continue;
      const parts = line.trim().split(/[, ]+/);
      if (parts.length >= 2) {
        const ip = parts[0];
        const port = parts[1];
        if (/^(?:\d{1,3}\.){3}\d{1,3}$/.test(ip) && /^\d+$/.test(port)) {
          ipPortCombinations.push(`${ip}:${port}`);
        }
      }
    }
    const allIps = [...new Set(ipPortCombinations)];

    // --- Calculate the slice of IPs for this specific job ---
    const chunkSize = Math.ceil(allIps.length / totalChunks);
    const startIndex = chunkIndex * chunkSize;
    const endIndex = startIndex + chunkSize;
    const ipsToCheck = allIps.slice(startIndex, endIndex);

    if (ipsToCheck.length === 0) {
      console.log(`Chunk ${chunkIndex + 1}/${totalChunks} is empty or out of bounds. Exiting.`);
      return;
    }

    console.log(`Job ${chunkIndex + 1}/${totalChunks}: Testing ${ipsToCheck.length} proxies (from index ${startIndex} to ${endIndex-1})...`);

    const promises = ipsToCheck.map(ipPort => {
      const [host, port] = ipPort.split(':');
      return testProxyWithRetries(host, parseInt(port, 10));
    });

    const results = await Promise.allSettled(promises);
    const workingProxies = [];
    results.forEach((result) => {
      if (result.status === 'fulfilled' && result.value.success) {
        // We only need the IP:Port string for the final file
        const originalIP = allIps[startIndex + workingProxies.length];
        workingProxies.push(originalIP);
      }
    });
    
    console.log(`Job ${chunkIndex + 1}/${totalChunks} finished. Found ${workingProxies.length} working proxies.`);

    if (workingProxies.length > 0) {
      fs.writeFileSync('working_proxies_partial.txt', workingProxies.join('\n') + '\n');
    }

  } catch (error) {
    console.error('An unexpected error occurred in test-proxies.js:', error);
    process.exit(1);
  }
}

main();
